import { cn } from '@/utils/cn';
import type { HTMLAttributes } from 'astro/types';

export interface Props extends HTMLAttributes<'button'> {}

const { class: className, ...props } = Astro.props;

const classes = cn(
  'inline-flex h-10 items-center justify-center rounded-md bg-primary px-4 py-2 text-sm font-medium text-primary-foreground ring-offset-background transition-colors',
  'hover:bg-primary/90',
  'focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2',
  'disabled:pointer-events-none disabled:opacity-50',
  className
);
---

<button
  type="button"
  class={classes}
  data-alert-dialog-action
  {...props}
>
  <slot />
</button>

